import "../Home/Home.scss";
import Header from "../../components/Header/Header";
import Hero from "../../components/Hero/Hero";
import Summary from "../../components/Summary/Summary";
import Comments from "../../components/Comments/Comments";
import CommentsForm from "../../components/CommentsForm/CommentsForm";
import Video from "../../components/Video/Video";
import React, { Component } from "react";
import comments from "../../data/video-details.json";
import likesIcon from "../../assets/icons/Icon-likes.svg";
import viewsIcon from "../../assets/icons/Icon-views.svg";
import axios from "axios";

export default class Main extends Component {
  state = {
    videos: [],
    vidoesDetailed: [],
    comments: [],
    timestamp: [],
    author: [],
    title: [],
    poster: [],
    summary: [],
    likes: [],
    views: [],
    likesIcon: likesIcon,
    viewsIcon: viewsIcon,
  };

  // Detailed API Call used to pull specific comment by ID

  clickVideo = (click) => {
    let id = click.id;
    console.log(id);
    axios
      .get(
        `https://project-2-api.herokuapp.com/videos/${id}?api_key=5f40c944-c6f0-45d0-b713-8ec93829e295`
      )
      .then((res) => {
        console.log(res);
        this.setState({
          poster: res.image,
          title: res.title,
          summary: res.data.description,
          likes: res.data.likes,
          views: res.views,
          author: res.data.channel,
          timestamp: res.data.timestamp,
          comments: res.data.comments,
        });
      });
  };

  // Intial display of video information
  // componentDidMount() {
  //   axios
  //     .get(
  //       `https://project-2-api.herokuapp.com/videos/1af0jruup5gu?api_key=5f40c944-c6f0-45d0-b713-8ec93829e295`
  //     )
  //     .then((res) => {
  //       console.log(res.data);
  //       console.log(res.data.image);
  //       console.log(res.data.title);
  //       this.setState({
  //         videosDetailed: res.data,
  //       });
  //     });
  // }

  // This occurs during first load of the page
  componentDidMount() {
    axios
      .get(
        `https://project-2-api.herokuapp.com/videos/?api_key=5f40c944-c6f0-45d0-b713-8ec93829e295`
      )
      .then((res) => {
        console.log(res.data);
        this.setState({
          videos: res.data,
          poster: res.data[0].image,
          title: res.data.title,
        });
      });
  }

  render() {
    return (
      <div>
        <Hero poster={this.state.poster} />
        <div className="desktop">
          <div className="desktop__left">
            <Summary
              title={this.state.title}
              summary={this.state.summary}
              likes={this.state.likes}
              views={this.state.views}
              likesIcon={likesIcon}
              viewsIcon={viewsIcon}
              author={this.state.author}
              timestamp={this.state.timestamp}
            />
            <CommentsForm />
            <Comments commentsArr={this.state.comments} />
          </div>
          <div className="desktop__right">
            <h3 className="video-sec">Next Video</h3>
            <Video
              videosArr={this.state.videos}
              clickHandle={this.clickVideo}
              posterVideo={this.state.poster}
              // filteredVideos={this.filteredVideo}
            />
          </div>
        </div>
      </div>
    );
  }
}
